package com.syntax.class02;

public class rectanglearea {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double num=5 ;
		double num2=4 ;
		double sum=num+num2 ;
		System.out.println("The sum of 2 number " +num+ " and " +num2+ " is equal to " +sum);
		
		double num3=3.9;
		double square=num3*num3;
		System.out.println("The square of the " +num3+ " is " +square);
		
		double width=5;
		double height=8;
		double answer=(width*2)+(height*2);
		double answer2=width*height;
		System.out.println("The perimeter of a rectangle with width "+width+" and height " +height+" is equal to " +answer+ " and the area is " +answer2);
	}

}
